tinymce.PluginManager.add('advimage', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('advimage', {
        text: '',
	title:'Картинка',
        icon: 'image',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Загрузка изображения',
                body: [
                    {type: 'textbox', name: 'title', label: 'Title'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('Title: ' + e.data.title);
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('advimage', {
        text: 'Картинка',
        context: 'tools',
	icon:'image',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'TinyMCE site',
                url: '../lib/js/tinymce/plugins/advimage/form.php',
                width: 250,
                height: 120,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                },
		{
		    text: 'Insert',
		    onclick:function(e){
			var win=editor.windowManager.getWindows()[0];
			editor.insertContent('<img src="'+win.getContentWindow().document.getElementById("image").value+'">');
		    }
		}]
            });
        }
    });
});
